,p_function_body_language=>'PLSQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  CURSOR documentos IS',
'    SELECT a.id_archivo                                                       AS id_archivo,',
'           b.tipo_documento_dig                                               AS tipo_documento_dig,',
'           a.tipo_arch                                                        AS tipo_arch,',
'           a.usr_alta                                                         AS usr_alta,',
'           to_char(a.fec_alta, ''dd/mm/yyyy hh24:mi:ss'')                       AS fec_alta,',
'           fn_api_url||''/file-storage/download/''||p.guid                    AS url,',
'           a.nro_doc,',
'           a.tipo_doc',
'    FROM  archivo_dig a, tipo_documento_dig b, pg_iw_archivo_dig p',
'    WHERE /*a.tipo_doc = :P16_TIPO_DOC',
'    AND */a.nro_doc = :P16_NRO_DOC --''4569878''',
'    AND (a.cod_tipo_documento_dig = :P16_TIPO_DOC_DIG',
'    OR :P16_TIPO_DOC_DIG IS NULL)',
'   /* AND a.usr_elim = '' ''*/',
'    AND a.cod_tipo_documento_dig = b.cod_tipo_documento_dig',
'    AND a.id_archivo = p.id_archivo',
'    ORDER BY a.fec_alta DESC;',
'  l_clob CLOB;',
'  l_nr NUMBER := 0;',
'BEGIN',
'  l_clob :=           ''<div class="contenedor">'';',
'  FOR i IN documentos LOOP',
'    l_nr := l_nr + 1;',
'    l_clob := l_clob || ''<a class="documento" target="_blank" href="''||i.url||''">'';',
'    l_clob := l_clob || ''<span class="doc-texto">''||i.tipo_documento_dig||''</span>'';',
'    l_clob := l_clob || ',
'                      CASE WHEN i.usr_alta != ''adminweb'' AND i.fec_alta IS NOT NULL THEN ',
'                        ''<span class="doc-data">''||',
'                          ''<div class="doc-data-text"><span class="fa fa-calendar-o"></span>''|| i.fec_alta || ''</div>'' || ',
'                          ''<div class="doc-data-text"><span class="fa fa-user"></span>''|| i.usr_alta || ''</div>''|| ',
'                        ''</span>'' ',
'                      END;',
'    l_clob := l_clob || case i.tipo_arch',
'      WHEN ''jpg'' THEN',
'        ''<img class="doc img" src="''||i.url||''">''',
'      WHEN ''png'' THEN',
'        ''<img class="doc img" src="''||i.url||''">''',
'      WHEN ''jpeg'' THEN',
'        ''<img class="doc img" src="''||i.url||''">''',
'      WHEN ''pdf'' THEN',
'        ''<span class="doc pdf fa fa-lg fa-file-pdf-o"></span>''',
'      WHEN ''doc'' THEN',
'        ''<span class="doc docx fa fa-lg fa-file-word-o"></span>''',
'      WHEN ''docx'' THEN',
'        ''<span class="doc docx fa fa-lg fa-file-word-o"></span>''',
'      WHEN ''xls'' THEN',
'        ''<span class="doc xlsx fa fa-lg fa-file-excel-o"></span>''',
'      WHEN ''xlsx'' THEN',
'        ''<span class="doc xlsx fa fa-lg fa-file-excel-o"></span>''',
'      ELSE ',
'        ''<span class="doc fa fa-lg fa-file-o"></span>''',
'    END;',
'      ',
'    l_clob := l_clob || ''</a>'';',
'  END LOOP;',
'  IF l_nr = 0 THEN',
'    l_clob := l_clob || ''<span>No se encontraron documentos digitalizados</span>'';',
'  END IF;',
'  l_clob := l_clob || ''</div>'';',
'  return l_clob;',
'END;'))
