,p_process_sequence=>20
,p_process_point=>'BEFORE_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Obtiene datos del solicitante'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'',
'DECLARE',
'  V_NRO_DOCUMENTO  SC_SOL.NRO_DOC%TYPE := :P17_NRO_DOCUMENTO;',
'  V_TIPO_DOCUMENTO SC_SOL.TIPO_DOC%TYPE := :P17_TIPO_DOCUMENTO;',
'  V_ID_TELEFONO    SC_SOL.ID_TELEFONO%TYPE;',
'BEGIN',
'  BEGIN',
'    IF FN_BANDERA THEN',
'      DECLARE',
'        L_COUNT   NUMBER;',
'        L_USUARIO VARCHAR2(8) := FN_USER;',
'        L_QUERY   CLOB;',
'        L_CURSOR  BINARY_INTEGER;',
'        L_NR      NUMBER;',
'      BEGIN',
'        L_QUERY  := q''~SELECT M.NRO_SOLI,',
'       ML1.APE_SOL || '', '' || ML1.NOM_SOL SOLICITANTE,',
'       (SELECT D.TIPO_SOL',
'          FROM SC_DET D',
'         WHERE D.NRO_SOL = M.NRO_SOL',
'           AND D.NRO_SOLI = M.NRO_SOLI) TIPO_SOLI,',
'       ML2.fec_soli fec_solicitud, ',
'       ML2.COD_SUCURSAL,',
'       (SELECT s.sucursal FROM sucursal s',
'       WHERE cod_sucursal = ML2.COD_SUCURSAL) sucursal,',
'       m.fec_alta fec_asignacion,',
'       M.RECORDATORIO,',
'       M.NRO_REFERENCIA,',
'       M.NRO_CONFIRMACION,',
'       M.NRO_SOL,',
'       ML1.APE_SOL,',
'       ML1.NOM_SOL,',
'       ML1.nro_doc,',
'       ML1.id_telefono,',
'       ML2.COD_VENDEDOR,',
'       ML2.ENT_INI_SOLI ENTREGA_INICIAL,',
'       ML2.MON_CUOTA_SOLI MONTO_CUOTA,',
'       ML2.CAN_CUOTA_SOLI CANT_COUTA,',
'       ML2.MON_SOLI MONTO_SOLICITUD',
'  FROM SC_SOL_CONFIRMACION M, SC_SOL ML1, SC ML2',
' WHERE ((M.INSTANCIA = ''INSI''))',
'   AND ((M.USR_PROC = ?))',
'   AND ((M.ESTADO_CONFIRMACION = ''PEND''))',
'   AND M.NRO_SOLI = ML2.NRO_SOLI',
'   AND M.NRO_SOL = ML1.NRO_SOL',
'   AND ML1.NRO_DOC = ?',
'   AND ML1.TIPO_DOC = ?~'';',
'        L_CURSOR := DBMS_HS_PASSTHROUGH.OPEN_CURSOR@WRK1;',
'        DBMS_HS_PASSTHROUGH.PARSE@WRK1(L_CURSOR, L_QUERY);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR, 1, L_USUARIO);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR,',
'                                               2,',
'                                               V_NRO_DOCUMENTO);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR,',
'                                               3,',
'                                               V_TIPO_DOCUMENTO);',
'        L_NR := DBMS_HS_PASSTHROUGH.FETCH_ROW@WRK1(L_CURSOR, FALSE);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 1, :P17_NRO_SOLI);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 2, :P17_SOLICITANTE);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 3, :P17_TIPO_SOLI);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 4, :P17_FEC_SOLICITUD);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 5, :P17_COD_SUCURSAL);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 6, :P17_SUCURSAL);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR,',
'                                           7,',
'                                           :P17_FEC_ASIGNACION);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 8, :P17_RECORDATORIO);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR,',
'                                           9,',
'                                           :P17_NRO_REFERENCIA);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR,',
'                                           10,',
'                                           :P17_NRO_CONFIRMACION);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 11, :P17_NRO_SOL);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 12, :P17_APELLIDO);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 13, :P17_NOMBRE);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR,',
'                                           14,',
'                                           :P17_NRO_DOCUMENTO);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 15, V_ID_TELEFONO);',
'         DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 16,:P17_COD_VENDEDOR);',
'           DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 17,:P17_ENTREGA_INICIAL);',
'           DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 18,:P17_MONTO_CUOTA);',
'           DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 19,:P17_CANT_COUTA);',
'           DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 20,:P17_MONTO_SOLICITUD);                ',
'      END;',
'    ELSE',
'      BEGIN',
'        SELECT M.NRO_SOLI,',
'               ML1.APE_SOL || '', '' || ML1.NOM_SOL SOLICITANTE,',
'               (SELECT D.TIPO_SOL',
'                  FROM SC_DET D',
'                 WHERE D.NRO_SOL = M.NRO_SOL',
'                   AND D.NRO_SOLI = M.NRO_SOLI) TIPO_SOLI,',
'               ML2.FEC_SOLI FEC_SOLICITUD,',
'               ML2.COD_SUCURSAL,',
'               (SELECT S.SUCURSAL',
'                  FROM SUCURSAL S',
'                 WHERE COD_SUCURSAL = ML2.COD_SUCURSAL) SUCURSAL,',
'               M.FEC_ALTA FEC_ASIGNACION,',
'               M.RECORDATORIO,',
'               M.NRO_REFERENCIA,',
'               M.NRO_CONFIRMACION,',
'               M.NRO_SOL,',
'               ML1.APE_SOL,',
'               ML1.NOM_SOL,',
'               ML1.NRO_DOC,',
'               ML1.ID_TELEFONO,',
'               ML2.COD_VENDEDOR,',
'               ML2.ENT_INI_SOLI ENTREGA_INICIAL,',
'               ML2.MON_CUOTA_SOLI MONTO_CUOTA,',
'               ML2.CAN_CUOTA_SOLI CANT_COUTA,',
'               ML2.MON_SOLI MONTO_SOLICITUD',
'          INTO :P17_NRO_SOLI,',
'               :P17_SOLICITANTE,',
'               :P17_TIPO_SOLI,',
'               :P17_FEC_SOLICITUD,',
'               :P17_COD_SUCURSAL,',
'               :P17_SUCURSAL,',
'               :P17_FEC_ASIGNACION,',
'               :P17_RECORDATORIO,',
'               :P17_NRO_REFERENCIA,',
'               :P17_NRO_CONFIRMACION,',
'               :P17_NRO_SOL,',
'               :P17_APELLIDO,',
'               :P17_NOMBRE,',
'               :P17_NRO_DOCUMENTO,',
'               V_ID_TELEFONO,',
'               :P17_COD_VENDEDOR,',
'               :P17_ENTREGA_INICIAL,',
'               :P17_MONTO_CUOTA,',
'               :P17_CANT_COUTA,',
'               :P17_MONTO_SOLICITUD',
'          FROM SC_SOL_CONFIRMACION M, SC_SOL ML1, SC ML2',
'         WHERE ((M.INSTANCIA = ''INSI''))',
'           AND ((M.USR_PROC = FN_USER))',
'           AND ((M.ESTADO_CONFIRMACION = ''PEND''))',
'           AND M.NRO_SOLI = ML2.NRO_SOLI',
'           AND M.NRO_SOL = ML1.NRO_SOL',
'           AND ML1.NRO_DOC = V_NRO_DOCUMENTO',
'           AND ML1.TIPO_DOC = V_TIPO_DOCUMENTO;',
'      END;',
'    END IF;',
'  END;',
'',
'  DECLARE',
'    CURSOR CUR_TELEFONOS IS',
'      SELECT DECODE(S.COD_TELEFONICA,',
'                    ''--'',',
'                    ''0'' || S.NRO_TELEFONO,',
'                    LPAD(TRIM(SC.PREFIJO) || S.NRO_TELEFONO, 10, 0)) NRO_TELEFONO,',
