,p_process_name=>'FETCH_CHAT'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_usuario msi_mensaje_lectura.leido_por%TYPE := fn_user;',
'BEGIN',
'  BEGIN',
'    APEX_COLLECTION.CREATE_OR_TRUNCATE_COLLECTION(p_collection_name => ''CHAT'');',
'  EXCEPTION',
'      WHEN dup_val_on_index THEN',
'        NULL;',
'  END;',
'  FOR i IN (SELECT id_mensaje,',
'            			 de,',
'            			 grupo_id,',
'            			 mensaje,',
'            	     to_char(m.fec_recepcion_server,''dd/mm/rrrr hh24:mi'') fec_server,',
'            			 CASE',
'            				 WHEN de = ''ingres'' THEN',
'            					''''',
'            				 ELSE',
'            					rh.puesto',
'            			 END AS puesto,',
'            			 tipo_mensaje',
'            FROM msi_mensaje m',
'            LEFT JOIN ingrid_control_usuario usu',
'            ON m.de = usu.login',
'            LEFT JOIN personal per',
'            ON usu.cod_personal = per.cod_personal',
'            LEFT JOIN rh_puesto rh',
'            ON per.cod_puesto = rh.cod_puesto',
'            WHERE m.grupo_id = :P26_NRO_SOLI',
'            ORDER BY m.fec_recepcion_server,',
'            				 m.id_mensaje)',
'  LOOP',
'    APEX_COLLECTION.ADD_MEMBER(',
'      p_collection_name => ''CHAT'',',
'      p_c001 => i.id_mensaje,  ',
'      p_c002 => i.de,',
'      p_c003 => i.grupo_id,',
'      p_c004 => i.mensaje,',
'      p_c005 => i.fec_server,',
'      p_c006 => i.puesto,',
'      p_c007 => i.tipo_mensaje',
'    );',
'  END LOOP;',
'  COMMIT;',
'  FOR mensaje IN (SELECT c001 id_mensaje',
'                  FROM APEX_COLLECTIONS',
'                  WHERE collection_name = ''CHAT'')',
'  LOOP',
'    DECLARE',
'      dummy CHAR(1);',
'    BEGIN',
'      SELECT 1',
'      INTO dummy',
'      FROM msi_mensaje_lectura',
'      WHERE id_mensaje = mensaje.id_mensaje',
'      AND leido_por = l_usuario;',
'    EXCEPTION',
'      WHEN No_Data_Found THEN',
'        DECLARE',
'          l_sysdate DATE := SYSDATE;',
'          l_sysdate_epoch NUMBER := to_epoch(l_sysdate);',
'        BEGIN',
'          INSERT INTO msi_mensaje_lectura (',
'            leido_por, fec_lectura, fec_lectura_epoch, id_mensaje,',
'            serial_instal, usr_alta, fec_alta',
'          ) VALUES (',
'            l_usuario, l_sysdate, l_sysdate_epoch, mensaje.id_mensaje,',
'            0, l_usuario, l_sysdate',
'          );',
'        END;',
'    END;     ',
'  END lOOP;',
'  COMMIT;',
'END;'))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>179031495743157365
);
