wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(198712704815996697)
,p_process_sequence=>20
,p_process_point=>'BEFORE_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Obtiene datos del solicitante'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  V_NRO_DOCUMENTO  SC_SOL.NRO_DOC%TYPE := :P57_NRO_DOCUMENTO;',
'  V_TIPO_DOCUMENTO SC_SOL.TIPO_DOC%TYPE := :P57_TIPO_DOCUMENTO;',
'  V_ID_TELEFONO    SC_SOL.ID_TELEFONO%TYPE;',
'BEGIN',
'  BEGIN',
'    IF FN_BANDERA THEN',
'      DECLARE',
'        L_COUNT   NUMBER;',
'        L_USUARIO VARCHAR2(8) := FN_USER;',
'        L_QUERY   CLOB;',
'        L_CURSOR  BINARY_INTEGER;',
'        L_NR      NUMBER;',
'      BEGIN',
'        L_QUERY  := q''~SELECT M.NRO_SOLI,',
'       ML1.APE_SOL || '', ''|| ML1.NOM_SOL SOLICITANTE,',
'       (SELECT D.TIPO_SOL',
'          FROM SC_DET D',
'         WHERE D.NRO_SOL = M.NRO_SOL',
'           AND D.NRO_SOLI = M.NRO_SOLI) TIPO_SOLI,',
'       ML2.fec_soli fec_solicitud, ',
'       ML2.COD_SUCURSAL,',
'       (SELECT s.sucursal FROM sucursal s',
'       WHERE cod_sucursal = ML2.COD_SUCURSAL) sucursal,',
'       m.fec_alta fec_asignacion,',
'       M.NRO_REFERENCIA,',
'       M.NRO_CONFIRMACION,',
'       M.NRO_SOL,',
'       ML1.APE_SOL,',
'       ML1.NOM_SOL,',
'       ML1.nro_doc,',
'       ML1.id_telefono,',
'       ML2.COD_VENDEDOR,',
'       ML2.ENT_INI_SOLI ENTREGA_INICIAL,',
'       ML2.MON_CUOTA_SOLI MONTO_CUOTA,',
'       ML2.CAN_CUOTA_SOLI CANT_COUTA,',
'       ML2.MON_SOLI MONTO_SOLICITUD',
'  FROM SC_SOL_CONFIRMACION_PEND_VU M,',
'       SC_SOL                      ML1,',
'       SC                          ML2,',
'       SC_SOL_CONFIRMACION         CONF',
' WHERE ((M.INSTANCIA = ''INSI''))',
'   AND ((M.login = ?))',
'   AND ((M.ESTADO_CONFIRMACION = ''PEND''))',
'   AND M.NRO_SOLI = ML2.NRO_SOLI',
'   AND M.NRO_SOL = ML1.NRO_SOL',
'   AND M.NRO_SOLI = CONF.NRO_SOLI',
'   AND ML2.NRO_SOLI = CONF.NRO_SOLI',
'   AND ML1.NRO_SOL = CONF.NRO_SOL',
'   AND ML1.NRO_DOC = ?',
'   AND ML1.TIPO_DOC = ?~'';',
'        L_CURSOR := DBMS_HS_PASSTHROUGH.OPEN_CURSOR@WRK1;',
'        DBMS_HS_PASSTHROUGH.PARSE@WRK1(L_CURSOR, L_QUERY);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR, 1, L_USUARIO);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR,',
'                                               2,',
'                                               V_NRO_DOCUMENTO);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR,',
'                                               3,',
'                                               V_TIPO_DOCUMENTO);',
'        L_NR := DBMS_HS_PASSTHROUGH.FETCH_ROW@WRK1(L_CURSOR, FALSE);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 1, :P57_NRO_SOLI);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 2, :P57_SOLICITANTE);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 3, :P57_TIPO_SOLI);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 4, :P57_FEC_SOLICITUD);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 5, :P57_COD_SUCURSAL);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 6, :P57_SUCURSAL);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR,',
'                                           7,',
'                                           :P57_FEC_ASIGNACION);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR,',
'                                           8,',
'                                           :P57_NRO_REFERENCIA);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR,',
'                                           9,',
'                                           :P57_NRO_CONFIRMACION);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 10, :P57_NRO_SOL);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 11, :P57_APELLIDO);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 12, :P57_NOMBRE);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR,',
'                                           13,',
'                                           :P57_NRO_DOCUMENTO);',
'        DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 14, V_ID_TELEFONO);',
'         DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 15,:P57_COD_VENDEDOR);',
'           DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 16,:P57_ENTREGA_INICIAL);',
'           DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 17,:P57_MONTO_CUOTA);',
'           DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 18,:P57_CANT_COUTA);',
'           DBMS_HS_PASSTHROUGH.GET_VALUE@WRK1(L_CURSOR, 19,:P57_MONTO_SOLICITUD);                ',
'      END;',
'    ELSE',
'      BEGIN',
'        SELECT M.NRO_SOLI,',
'               ML1.APE_SOL || '', '' || ML1.NOM_SOL SOLICITANTE,',
'               (SELECT D.TIPO_SOL',
'                  FROM SC_DET D',
'                 WHERE D.NRO_SOL = M.NRO_SOL',
'                   AND D.NRO_SOLI = M.NRO_SOLI) TIPO_SOLI,',
'               ML2.fec_soli fec_solicitud, ',
'               ML2.COD_SUCURSAL,',
'               (SELECT s.sucursal FROM sucursal s',
'               WHERE cod_sucursal = ML2.COD_SUCURSAL) sucursal,',
'               m.fec_alta fec_asignacion,',
'               M.NRO_REFERENCIA,',
'               M.NRO_CONFIRMACION,',
'               M.NRO_SOL,',
'               ML1.APE_SOL,',
'               ML1.NOM_SOL,',
'               ML1.nro_doc,',
'               ML1.id_telefono,',
'               ML2.COD_VENDEDOR,',
'               ML2.ENT_INI_SOLI ENTREGA_INICIAL,',
'               ML2.MON_CUOTA_SOLI MONTO_CUOTA,',
'               ML2.CAN_CUOTA_SOLI CANT_COUTA,',
'               ML2.MON_SOLI MONTO_SOLICITUD',
'          INTO :P57_NRO_SOLI,',
'               :P57_SOLICITANTE,',
'               :P57_TIPO_SOLI,',
'               :P57_FEC_SOLICITUD,',
'               :P57_COD_SUCURSAL,',
'               :P57_SUCURSAL,',
'               :P57_FEC_ASIGNACION,',
'               :P57_NRO_REFERENCIA,',
'               :P57_NRO_CONFIRMACION,',
'               :P57_NRO_SOL,',
'               :P57_APELLIDO,',
'               :P57_NOMBRE,',
'               :P57_NRO_DOCUMENTO,',
'               V_ID_TELEFONO,',
'               :P57_COD_VENDEDOR,',
'               :P57_ENTREGA_INICIAL,',
'               :P57_MONTO_CUOTA,',
'               :P57_CANT_COUTA,',
'               :P57_MONTO_SOLICITUD',
'          FROM SC_SOL_CONFIRMACION M, SC_SOL ML1, SC ML2',
'         WHERE ((M.INSTANCIA = ''INSI''))',
'           AND ((M.USR_PROC = FN_USER))',
'           AND ((M.ESTADO_CONFIRMACION = ''PEND''))',
'           AND M.NRO_SOLI = ML2.NRO_SOLI',
'           AND M.NRO_SOL = ML1.NRO_SOL',
'           AND ML1.NRO_DOC = V_NRO_DOCUMENTO',
'           AND ML1.TIPO_DOC = V_TIPO_DOCUMENTO;',
'      END;',
'    END IF;',
'  END;',
'',
'  DECLARE',
'    CURSOR CUR_TELEFONOS IS',
'      SELECT DECODE(S.COD_TELEFONICA,',
'                    ''--'',',
'                    ''0'' || S.NRO_TELEFONO,',
'                    LPAD(TRIM(SC.PREFIJO) || S.NRO_TELEFONO, 10, 0)) NRO_TELEFONO,',
'             ''<i class="fa fa-phone"></i>'' ICONO',
'        FROM SC_TELEFONO S, SC_TELEFONICA SC',
'       WHERE S.COD_TELEFONICA = SC.COD_TELEFONICA',
'         AND S.ID_TELEFONO = V_ID_TELEFONO;',
'  ',
'  BEGIN',
'    APEX_COLLECTION.CREATE_OR_TRUNCATE_COLLECTION(P_COLLECTION_NAME => ''TMP_TELEFONOS'');',
'    FOR A IN CUR_TELEFONOS LOOP',
'      APEX_COLLECTION.ADD_MEMBER(P_COLLECTION_NAME => ''TMP_TELEFONOS'',',
'                                 P_C001            => A.NRO_TELEFONO,',
'                                 P_C002            => A.ICONO);',
'    END LOOP;',
'  END;',
'',
'',
'',
'   :P57_ENTREGA_INICIAL := TO_CHAR(:P57_ENTREGA_INICIAL, ''999G999G999G999G999G999G990'');',
'   :P57_MONTO_CUOTA := TO_CHAR(:P57_MONTO_CUOTA, ''999G999G999G999G999G999G990'');',
'   :P57_MONTO_SOLICITUD := TO_CHAR(:P57_MONTO_SOLICITUD, ''999G999G999G999G999G999G990'');',
'',
'END;',
''))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>198712704815996697
);
