,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'BEGIN',
'',
'  SELECT (SELECT L.COD_DEPTO',
'            FROM LOCALIDAD L',
'           WHERE L.COD_LOCALIDAD = S.COD_LOCALIDAD),',
'         S.COD_LOCALIDAD,',
'         S.COD_BARRIO,',
'         S.DOMICILIO,',
'         S.TIPO_VIVIENDA,',
'         S.CROQUIS_HABLADO',
'    INTO :P57_COD_DEPARTAMENTO,',
'         :P57_COD_LOCALIDAD,',
'         :P57_COD_BARRIO,',
'         :P57_DOMICILIO,',
'         :P57_TIPO_VIVIENDA,',
'         :P57_CROQUIS_HABLADO',
'    FROM SC_SOL S',
'   WHERE NRO_SOL = :P57_NRO_SOL;',
'EXCEPTION',
'  WHEN NO_DATA_FOUND THEN',
'    :P57_COD_DEPARTAMENTO := NULL;',
'    :P57_COD_LOCALIDAD    := NULL;',
'    :P57_COD_BARRIO       := NULL;',
'    :P57_DOMICILIO        := NULL;',
'    :P57_TIPO_VIVIENDA    := NULL;',
'    :P57_CROQUIS_HABLADO  := NULL;',
'  WHEN OTHERS THEN',
'    :P57_COD_DEPARTAMENTO := NULL;',
'    :P57_COD_LOCALIDAD    := NULL;',
'    :P57_COD_BARRIO       := NULL;',
'    :P57_DOMICILIO        := NULL;',
'    :P57_TIPO_VIVIENDA    := NULL;',
'    :P57_CROQUIS_HABLADO  := NULL;',
'END;',
'',
'BEGIN',
'  SELECT ''('' || L.COD_LOCALIDAD || '') '' || L.LOCALIDAD LOCALIDAD',
'    INTO :P57_LOCALIDAD',
'    FROM LOCALIDAD L',
'   WHERE COD_DEPTO = :P57_COD_DEPARTAMENTO',
'     AND L.COD_LOCALIDAD = :P57_COD_LOCALIDAD;',
'EXCEPTION',
'  WHEN NO_DATA_FOUND THEN',
'    :P57_LOCALIDAD := NULL;',
'  WHEN OTHERS THEN',
'    :P57_LOCALIDAD := NULL;',
'END;',
'',
'BEGIN',
'  SELECT ''('' || COD_BARRIO || '') '' || BARRIO',
'    INTO :P57_BARRIO',
'    FROM BARRIO B',
'   WHERE B.COD_LOCALIDAD = :P57_COD_LOCALIDAD',
'     AND B.COD_DEPTO = :P57_COD_DEPARTAMENTO',
'     AND COD_BARRIO = :P57_COD_BARRIO;',
'EXCEPTION',
'  WHEN NO_DATA_FOUND THEN',
'    :P57_BARRIO := NULL;',
'  WHEN OTHERS THEN',
'    :P57_BARRIO := NULL;',
'END;',
''))
,p_process_clob_language=>'PLSQL'
,p_internal_uid=>198713543529996698
);
wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(198713988577996698)
,p_process_sequence=>40
,p_process_point=>'BEFORE_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Obtiene datos laborales'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT nro_item_confirmacion,',
'             tipo_confirmacion,',
'             NVL(nro_item_trabajo,0),',
'             estado_confirmacion,',
'             descripcion',
'             into :P57_NRO_ITEM_CONFIRMACION,',
'                 :P57_TIPO_CONFIRMACION,',
'                 :P57_NRO_ITEM_TRABAJO,',
'                 :P57_ESTADO_CONFIRMACION,',
'                 :P57_DESCRIPCION',
'          FROM sc_sol_confirm_telef_vu',
'         WHERE nro_soli = :P57_NRO_SOLI',
'           AND nro_confirmacion = :P57_NRO_CONFIRMACION',
'         ORDER BY nro_item_confirmacion;',
'',
'',
'DECLARE',
'  V_ID_TELEFONO SC_SOL_TRABAJO.ID_TELEFONO%TYPE;',
'BEGIN',
'  BEGIN',
'    SELECT A.DENOMINACION EMPRESA,',
'           A.DIRECCION DIRECCION,',
'           A.COD_LOCALIDAD LOCALIDAD,',
'           A.OBSERVACION,',
'           NRO_ITEM,',
'           (SELECT UPPER(B.ACTIVIDAD)',
'              FROM SC_ACTIVIDAD B',
'             WHERE B.COD_ACTIVIDAD = A.COD_ACTIVIDAD FETCH FIRST 1 ROWS ONLY) ACTIVIDAD,',
'           A.ID_TELEFONO',
'      INTO :P57_DENOMINACION,',
'           :P57_DIRECCION_LABORAL,',
'           :P57_COD_LOCALIDAD_LABORAL,',
'           :P57_OBSERVACION_LABORAL,',
'           :P57_NRO_ITEM,',
'           :P57_ACTIVIDAD,',
'           V_ID_TELEFONO',
'      FROM SC_SOL_TRABAJO A',
'     WHERE A.NRO_SOL = :P57_NRO_SOL',
'       AND A.NRO_SOLI = :P57_NRO_SOLI',
'       AND A.ESTADO = 1',
'       and a.nro_item = :P57_NRO_ITEM_TRABAJO',
'       ORDER BY A.NRO_ITEM DESC FETCH FIRST 1 ROWS ONLY;',
'  EXCEPTION',
'    WHEN NO_DATA_FOUND THEN',
'      :P57_DENOMINACION          := NULL;',
'      :P57_DIRECCION_LABORAL     := NULL;',
'      :P57_COD_LOCALIDAD_LABORAL := NULL;',
'      :P57_OBSERVACION_LABORAL   := NULL;',
'      :P57_NRO_ITEM              := NULL;',
'      :P57_ACTIVIDAD             := NULL;',
'    WHEN OTHERS THEN',
'      :P57_DENOMINACION          := NULL;',
'      :P57_DIRECCION_LABORAL     := NULL;',
'      :P57_COD_LOCALIDAD_LABORAL := NULL;',
'      :P57_OBSERVACION_LABORAL   := NULL;',
'      :P57_NRO_ITEM              := NULL;',
'      :P57_ACTIVIDAD             := NULL;',
'  END;',
'',
'  DECLARE',
'    CURSOR CUR_TELEFONOS IS',
'      SELECT DECODE(S.COD_TELEFONICA,',
'                    ''--'',',
'                    ''0'' || S.NRO_TELEFONO,',
'                    LPAD(TRIM(SC.PREFIJO) || S.NRO_TELEFONO, 10, 0)) NRO_TELEFONO,',
'             ''<i class="fa fa-phone"></i>'' ICONO',
'        FROM SC_TELEFONO S, SC_TELEFONICA SC',
'       WHERE S.COD_TELEFONICA = SC.COD_TELEFONICA',
'         AND S.ID_TELEFONO = V_ID_TELEFONO;',
'  ',
'  BEGIN',
'    APEX_COLLECTION.CREATE_OR_TRUNCATE_COLLECTION(P_COLLECTION_NAME => ''TMP_TELEFONOS_LABORAL'');',
'    FOR A IN CUR_TELEFONOS LOOP',
'      APEX_COLLECTION.ADD_MEMBER(P_COLLECTION_NAME => ''TMP_TELEFONOS_LABORAL'',',
'                                 P_C001            => A.NRO_TELEFONO,',
'                                 P_C002            => A.ICONO);',
'    END LOOP;',
'  END;',
