wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(211864167338929651)
,p_process_sequence=>20
,p_process_point=>'BEFORE_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Obtiene datos del solicitante'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  V_NRO_DOCUMENTO  SC_SOL.NRO_DOC%TYPE := :P61_NRO_DOCUMENTO;',
'  V_TIPO_DOCUMENTO SC_SOL.TIPO_DOC%TYPE := :P61_TIPO_DOCUMENTO;',
'  V_ID_TELEFONO    SC_SOL.ID_TELEFONO%TYPE;',
'BEGIN',
'  BEGIN',
'    IF FN_BANDERA THEN',
'      DECLARE',
'        L_COUNT   NUMBER;',
'        L_USUARIO VARCHAR2(8) := FN_USER;',
'        L_QUERY   CLOB;',
'        L_CURSOR  BINARY_INTEGER;',
'        L_NR      NUMBER;',
'      BEGIN',
'        L_QUERY  := q''~SELECT S.NRO_SOLI,',
'       ML1.APE_SOL || '', '' || ML1.NOM_SOL SOLICITANTE,',
'       (SELECT D.TIPO_SOL',
'          FROM SC_DET D',
'         WHERE D.NRO_SOL = S.NRO_SOL',
'           AND D.NRO_SOLI = S.NRO_SOL) TIPO_SOLI,',
'       ML2.FEC_SOLI FEC_SOLICITUD,',
'       ML2.COD_SUCURSAL,',
'       (SELECT SU.SUCURSAL',
'          FROM SUCURSAL SU',
'         WHERE SU.COD_SUCURSAL = ML2.COD_SUCURSAL) SUCURSAL,',
'         s.FEC_ALTA FEC_ASIGNACION,',
'         s.RECORDATORIO,',
'         s.NRO_REFERENCIA,',
'         s.NRO_CONFIRMACION,',
'         s.NRO_SOL,',
'         ML1.APE_SOL,',
'         ML1.NOM_SOL,',
'         ML1.NRO_DOC,',
'          ML1.id_telefono,',
'       ML2.COD_VENDEDOR,',
'       ML2.ENT_INI_SOLI ENTREGA_INICIAL,',
'       ML2.MON_CUOTA_SOLI MONTO_CUOTA,',
'       ML2.CAN_CUOTA_SOLI CANT_COUTA,',
'       ML2.MON_SOLI MONTO_SOLICITUD',
'  FROM SC_SOL_CONFIRMACION S, SC_SOL ML1, SC ML2',
' WHERE S.usr_PROC = ?',
'   AND S.NRO_SOL = ML1.NRO_SOL',
'   AND S.NRO_SOLI =  ML2.NRO_SOLI',
'   AND TRUNC(fec_fin_proc) = TRUNC(CURRENT_DATE)',
'   AND s.instancia = ''INSI''',
'   AND ML1.NRO_DOC = ?',
'   AND ML1.TIPO_DOC = ?~'';',
'        L_CURSOR := DBMS_HS_PASSTHROUGH.OPEN_CURSOR@WRK1;',
'        DBMS_HS_PASSTHROUGH.PARSE@WRK1(L_CURSOR, L_QUERY);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR, 1, L_USUARIO);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR,',
'                                               2,',
'                                               V_NRO_DOCUMENTO);',
'        DBMS_HS_PASSTHROUGH.BIND_VARIABLE@WRK1(L_CURSOR,',
'                                               3,',
