'  v_item             NUMBER := 0; ',
'',
'	CURSOR crreport IS',
'		SELECT jrd_name FROM jrxml_report_definitions WHERE jrd_id = 2402;',
'	vcname  VARCHAR2(200);',
'	rparam  pk_jrxml2pdf_repgen.tparameter;',
