'    v_sum_o_res                 NUMBER; ',
'    v_cod_motivo_ajuste         motivos_ajustes_stk.cod_motivo_ajuste%TYPE      := :P6_COD_MOTIVO_AJUSTE;',
'    v_motivo_ajuste             motivos_ajustes_stk.motivo_ajuste%TYPE          := :P6_MOTIVO_AJUSTE;',
'    v_consumo_empresa           NUMBER := :P6_CONSUMO_EMPRESA;',
'    v_imprime_automatico        NUMBER := :P6_IMPRIME_AUTOMATICO;',
'    g_fec_ingrid                DATE := CURRENT_DATE;',
'    g_usuario                   VARCHAR(8) := FN_USER;',
'    v_item                      NUMBER := :P6_ULT_ITEM_ART;',
'    v_codigo                    adu_aplicacion.codigo%TYPE  := 208;',
'    v_recurso                   adu_menu_recurso.recurso%TYPE := ''FN_203'';',
'    v_resultado                 BOOLEAN;          ',
'    l_fecha DATE;',
'    l_cod_var VARCHAR2 (1000);',
'',
'BEGIN',
'    pkg_ic.inicializar;',
'    g_fec_ingrid := pkg_ic.g_fec_ingrid;',
'    g_usuario    := pkg_ic.g_usuario;',
'',
'    SELECT COUNT(d.nro_item_art) INTO v_item',
'      FROM ajustes_stock_art d, articulos dl1',
'     WHERE d.cod_articulo = dl1.cod_articulo',
'        AND d.cod_sucursal = :P6_COD_SUCURSAL ',
'        AND d.fec_ajuste = :P6_FEC_AJUSTE ',
'        AND d.nro_ajuste = :P6_NRO_AJUSTE;',
'',
'    IF v_item = 0 THEN',
unistr('        RAISE_APPLICATION_ERROR(-20001,''Verifique, No puede Registrar si no posee art\00EDculos en el detalle del consumo...'');'),
'    END IF;',
'',
'    BEGIN',
'        SELECT NVL(a.comentario,''.''),',
'            b.sum_o_res,      ',
'            b.cod_motivo_ajuste,',
'            b.motivo_ajuste,',
'            c.consumo_empresa,',
'            c.imprime_automatico ',
