'                RAISE_APPLICATION_ERROR(-20002, ''Error al Seleccionar Motivo de Ajuste...'');       ',
'    ',
'    END;   ',
'    COMMIT;',
'',
'    IF v_consumo_empresa = 0 THEN',
'       Raise_Application_Error(-20003, ''No corresponde a Consumo Empresa'');',
'    END IF;',
'',
'    pr_regajustestk(p_cod_sucursal => v_cod_sucursal,',
'                    p_fec_ajuste   => v_fec_ajuste,',
'                    p_nro_ajuste   => v_nro_ajuste);',
'',
' pkg_bal_asientos_contables#01.pr_bal04100(',
'    p_cod_sucursal =>  v_cod_sucursal,',
'    p_fecha        =>  g_fec_ingrid,',
'    p_numero       =>  v_nro_ajuste,',
'    p_cod_tipo_asto => ''AJST'',',
'    p_numero2       =>  0,',
'    p_tipo_f4       =>  0,',
'    p_fecha2        => l_fecha,',
'    p_cod_varchar   => l_cod_var); ',
'    COMMIT; ',
'    ',
'    :P6_IMPRIME_AUTOMATICO := v_imprime_automatico; ',
'     ',
'END;'))
,p_process_clob_language=>'PLSQL'
,p_process_error_message=>'#SQLERRM_TEXT#'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(219125383257925812)
,p_internal_uid=>226042446840474943
