,p_process_name=>'UPDATE'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'    v_cod_sucursal          ajustes_stock_art.cod_sucursal%TYPE     := :P9_COD_SUCURSAL;',
'    v_fec_ajuste            ajustes_stock_art.fec_ajuste%TYPE       := :P9_FEC_AJUSTE;',
'    v_nro_ajuste            ajustes_stock_art.nro_ajuste%TYPE       := :P9_NRO_AJUSTE;',
'    v_nro_item_art          ajustes_stock_art.nro_item_art%TYPE     := :P9_NRO_ITEM_ART;',
'    v_cod_articulo          ajustes_stock_art.cod_articulo%TYPE     := :P9_COD_ARTICULO_AUX;',
'    v_cantidad              ajustes_stock_art.cantidad%TYPE         := :P9_CANTIDAD;',
'    v_costo_unit            ajustes_stock_art.costo_unit%TYPE       := :P9_COSTO_UNIT;',
'    v_fec_ult_act           ajustes_stock_art.fec_ult_act%TYPE      := CURRENT_DATE;',
'    v_cant_reservada        articulos_suc_depo.cant_reservada%TYPE  := :P9_CANT_RESERVADA;',
'    g_usuario               varchar2(8):= lower(v(''app_user''));',
'    v_cod_motivo_ajuste     VARCHAR2(100) := :P9_COD_MOTIVO_AJUSTE;',
'    v_cod_tipo_mercaderia   VARCHAR(100);',
'    v_mueve_stock           NUMBER;',
'    v_vehiculo              NUMBER;',
'    g_rows                  NUMBER;',
'    v_sum_o_res     NUMBER  :=  :P9_SUM_O_RES;',
'    v_cantidad_aux  NUMBER  :=  :P9_STOCK - :P9_CANT_RESERVADA - :P9_CANTIDAD;',
'',
'BEGIN',
'    UPDATE ajustes_stock_art',
'        SET ',
'        cod_sucursal = v_cod_sucursal, ',
'        fec_ajuste = v_fec_ajuste,',
'        nro_ajuste = v_nro_ajuste, ',
'        nro_item_art = v_nro_item_art,',
'        cod_articulo = v_cod_articulo, ',
'        cantidad = v_cantidad,',
'        costo_unit = v_costo_unit, ',
'        fec_ult_act = v_fec_ult_act,',
'        usr_ult_act = g_usuario',
'        WHERE cod_sucursal = v_cod_sucursal AND',
'        fec_ajuste = v_fec_ajuste AND',
'        nro_ajuste = v_nro_ajuste AND',
'        nro_item_art = v_nro_item_art;',
'',
'        BEGIN',
'    SELECT b.cod_tipo_mercaderia',
'    INTO v_cod_tipo_mercaderia',
'    FROM articulos a, familias b, motivos_ajustes_stk c',
'    WHERE a.cod_articulo = v_cod_articulo',
'        AND a.cod_familia = b.cod_familia',
'        AND b.cod_tipo_mercaderia = c.cod_tipo_mercaderia',
'        AND c.cod_motivo_ajuste = v_cod_motivo_ajuste;',
'',
'    EXCEPTION',
'      WHEN no_data_found THEN',
'        raise_application_error(-20999,''Verifique, No Coinciden el Tipo Mercaderia del Articulo ingresado con el motivo de ajuste...'');',
'      WHEN OTHERS THEN',
'        raise_application_error(-20999,''Error al Seleccionar Cod.Tipo Mercaderia...'' ||SQLERRM);',
'    END;',
'',
'    IF v_mueve_stock = 0 THEN',
'      ROLLBACK;',
'      raise_application_error(-20999,',
'                              ''No se permiten ajustes de items que no mueven stock...'');',
'    END IF;',
'',
'    IF v_vehiculo = 1 AND',
'       (v_cod_tipo_mercaderia != ''REPU'' OR v_cod_tipo_mercaderia != ''ELEC'') THEN',
'      raise_application_error(-20999,',
'                              ''No se Permite Consumo Empresa de Vehiculos...'');',
'    END IF;',
'',
'    BEGIN',
'      SELECT cod_sucursal',
'        INTO v_cod_sucursal',
'        FROM ajustes_stock_art',
'       WHERE cod_sucursal = v_cod_sucursal',
'         AND fec_ajuste = v_fec_ajuste',
'         AND nro_ajuste = v_nro_ajuste',
'         AND cod_articulo = v_cod_articulo;',
'      g_rows := 1;',
'    EXCEPTION',
'      WHEN no_data_found THEN',
'        g_rows := 0;',
'    END;',
'',
'      IF v_costo_unit <= 0 THEN',
'      ROLLBACK;',
'      raise_application_error(-20999,',
'                              ''Verifique el costo del articulo debe ser mayor a cero.'');',
'    END IF; ',
'',
'',
'    IF :P9_SUM_O_RES = 0  AND v_cantidad_aux < 0 THEN',
unistr('        RAISE_APPLICATION_ERROR(-20001, ''No podr\00E1 Registrar el Consumo... Verifique La cantidad Ingresada supera el stock disp. en el depo.'');'),
'    END IF;',
'END;',
' '))
