'  v_activo              NUMBER       := 1;',
'  v_consumo_empresa     NUMBER       := 1;',
'  v_fec_ajuste          DATE         := to_date(:P11_FEC_AJUSTE,''DDMMYYYY'');',
'  v_nro_ajuste          NUMBER;',
'  v_fec_ajuste_aux      DATE; ',
'  v_comentario          VARCHAR2(100);',
'  v_cod_deposito        NUMBER;',
'  v_fec_ult_act         DATE;',
'  v_usr_ult_act         VARCHAR2(100);',
'  l_contador            NUMBER := 1;',
'  v_cod_sucursal_d      NUMBER; ',
'  v_cod_sucursal_h      NUMBER; ',
'  v_cod_motivo_ajuste_d VARCHAR2(8); ',
'  v_cod_motivo_ajuste_h VARCHAR2(8); ',
'  v_fec_ajuste_d        DATE; ',
'  v_fec_ajuste_h        DATE; ',
'',
'BEGIN',
'      IF :P11_COD_SUCURSAL IS NULL THEN ',
'         v_cod_sucursal_d := 0;',
'         v_cod_sucursal_h := 999;',
'      ELSE ',
'          v_cod_sucursal_d := :P11_COD_SUCURSAL;',
'          v_cod_sucursal_h := :P11_COD_SUCURSAL;',
'      END IF; ',
'      ',
'      IF :P11_COD_MOTIVO_AJUSTE IS NULL THEN ',
'          v_cod_motivo_ajuste_d := ''        ''; ',
'          v_cod_motivo_ajuste_h := ''ZZZZZZZZ''; ',
'      ELSE ',
'          v_cod_motivo_ajuste_d := :P11_COD_MOTIVO_AJUSTE; ',
'          v_cod_motivo_ajuste_h := :P11_COD_MOTIVO_AJUSTE; ',
'      END IF; ',
'      ',
'      IF :P11_FEC_AJUSTE IS NULL THEN ',
'          v_fec_ajuste_d := ''01012000'';',
'          v_fec_ajuste_h := SYSDATE;  ',
'      ELSE ',
'          v_fec_ajuste_d := :P11_FEC_AJUSTE; ',
'          v_fec_ajuste_h := :P11_FEC_AJUSTE; ',
'      END IF; ',
'      --raise_application_error(-20999, ''v_cod_motivo_ajuste_d: '' || v_cod_motivo_ajuste_d || '' v_cod_motivo_ajuste_h '' || v_cod_motivo_ajuste_h);',
'      IF FN_BANDERA THEN',
'          DECLARE',
'              l_query  VARCHAR2(4000);',
'              l_cursor BINARY_INTEGER;',
'              l_nr     NUMBER := 0;',
'          BEGIN',
'            l_query  := q''!',
'                    SELECT',
'                      m.cod_sucursal, ',
'                      m.fec_ajuste,',
'                      m.nro_ajuste, ',
'                      m.cod_motivo_ajuste,',
'                      m.comentario, ',
'                      m.cod_deposito,',
'                      m.fec_ult_act, ',
'                      m.usr_ult_act',
'                    FROM ajustes_stock m,',
'                    motivos_ajustes_stk n,',
'                    ajustes_stk_tipos l',
'                    WHERE m.cod_sucursal >= ?',
'                    AND m.cod_sucursal <= ?',
'                    AND m.estado_ajuste =  ?',
'                    AND m.cod_motivo_ajuste >= ?  ',
'                    AND m.cod_motivo_ajuste <= ?',
'                    AND n.activo            = 1',
'                    AND l.consumo_empresa   = 1',
'                    AND m.fec_ajuste        >= ?',
'                    AND m.fec_ajuste        <= ?',
'                    ORDER BY m.cod_sucursal ASC, m.fec_ajuste ASC, m.nro_ajuste ASC, m.cod_motivo_ajuste, m.comentario, m.cod_deposito, m.fec_ult_act,  m.usr_ult_act     ',
'              !'';',
'            l_cursor := dbms_hs_passthrough.open_cursor@wrk1;',
'            dbms_hs_passthrough.parse@wrk1(l_cursor, l_query);',
'            dbms_hs_passthrough.bind_variable@wrk1(l_cursor, 1, v_cod_sucursal_d);',
'            dbms_hs_passthrough.bind_variable@wrk1(l_cursor, 2, v_cod_sucursal_h);',
'            dbms_hs_passthrough.bind_variable@wrk1(l_cursor, 3, v_estado_ajuste);',
'            dbms_hs_passthrough.bind_variable@wrk1(l_cursor, 4, v_cod_motivo_ajuste_d);',
'            dbms_hs_passthrough.bind_variable@wrk1(l_cursor, 5, v_cod_motivo_ajuste_h);       ',
'            dbms_hs_passthrough.bind_variable@wrk1(l_cursor, 6, v_fec_ajuste_d);',
'            dbms_hs_passthrough.bind_variable@wrk1(l_cursor, 7, v_fec_ajuste_h);',
'',
'            LOOP',
'                l_nr := dbms_hs_passthrough.fetch_row@wrk1(l_cursor, FALSE);',
'                EXIT WHEN l_nr = 0;',
'                dbms_hs_passthrough.get_value@wrk1(l_cursor, 1, v_cod_sucursal);         ',
'                dbms_hs_passthrough.get_value@wrk1(l_cursor, 2, v_fec_ajuste_aux);',
'                dbms_hs_passthrough.get_value@wrk1(l_cursor, 3, v_nro_ajuste);',
'                dbms_hs_passthrough.get_value@wrk1(l_cursor, 4, v_cod_motivo_ajuste);',
'                dbms_hs_passthrough.get_value@wrk1(l_cursor, 5, v_comentario);',
'                dbms_hs_passthrough.get_value@wrk1(l_cursor, 6, v_cod_deposito);',
'                dbms_hs_passthrough.get_value@wrk1(l_cursor, 7, v_fec_ult_act);',
'                dbms_hs_passthrough.get_value@wrk1(l_cursor, 8, v_usr_ult_act);',
'                                                            ',
'                APEX_COLLECTION.ADD_MEMBER(P_COLLECTION_NAME => ''TMP_STOCK'',',
'                    P_C001              => v_cod_sucursal,',
'                    P_C002              => v_fec_ajuste,',
'                    P_C003              => v_nro_ajuste,',
'                    P_C004              => v_cod_motivo_ajuste,',
'                    P_C005              => v_comentario,',
'                    P_C006              => v_cod_deposito,',
'                    P_C007              => v_fec_ult_act,',
'                    P_C008              => v_usr_ult_act',
'                );',
'            END LOOP;',
'            dbms_hs_passthrough.close_cursor@wrk1(l_cursor);',
'          END;',
'      ELSE        ',
'        NULL;',
'      END IF;',
'',
'      :P11_BAN := :P11_BAN + 1; ',
'END;'))
